sources.sass\\colors.scss:
$baseColor : red;
$hoverColor : purple;


@mixin setDarkenColor ($numerOfDark:0){
    background-color: darken($baseColor,$numerOfDark);
}

@mixin setLightenColor ($numerOfLight:0){
    background-color: lighten($baseColor, $numerOfLight);
}

@function changeColor() {
        
     @return $hoverColor;
 }


 @mixin changeColorToDark($numDark){
    background-color: darken($hoverColor,$numDark);
 }

 @mixin changeColorToLight($numLight){
    background-color: lighten($hoverColor,$numLight);
 }

 .base_color{
    background-color: $baseColor;

    &:hover {
        background-color: changeColor();
    }
}

.dark_1{
    @include setDarkenColor(10);
    &:hover{
        @include changeColorToDark(10);
    }
}
.dark_2{
    @include setDarkenColor(15);
    &:hover{
        @include changeColorToDark(15);
    }
}
.dark_3{
    @include setDarkenColor(20);
    &:hover{
        @include changeColorToDark(20);
    }
}
.dark_4{
    @include setDarkenColor(25);
    &:hover{
        @include changeColorToDark(25);
    }
}

.light_1{
    @include setLightenColor(10);
    &:hover{
        @include changeColorToLight(10);
    }
}
.light_2{
    @include setLightenColor(20);
    &:hover{
        @include changeColorToLight(20);
    }
}
.light_3{
    @include setLightenColor(30);
    &:hover{
        @include changeColorToLight(30);
    }
}
.light_4{
    @include setLightenColor(40);
    &:hover{
        @include changeColorToLight(40);
    }
}

